    });
}); /script  And finally, our controller action can be modified to return form status and
validation messages in JSON format if we see the 'isAjax' post parameter (which
was set in the JavaScript just before submit):  public function uploadFormAction()
{
    $form = new UploadForm('upload-form');

    $request = $this- getRequest();

    if (! $request- isPost()) {
        return ['form' =  $form];
    }

    // Make certain to merge the files info!
    $post = array_merge_recursive(
        $request- getPost()- toArray(),
        $request- getFiles()- toArray()
    );

    $form- setData($post);
    if ($form- isValid()) {
        $data = $form- getData();

        // Form is valid, save the form!
        if (! empty($post['isAjax'])) {
            return new JsonModel(array(
                'status'   =  true,
                'redirect' =  $this- url()- fromRoute('upload-form/success'),
                'formData' =  $data,
            ));
        }

        // Fallback for non-JS clients
        return $this- redirect()- toRoute('upload-form/success');
    }

    if (! empty($post['isAjax'])) {
        // Send back failure information via JSON
        return new JsonModel([
            'status'     =  false,
            'formErrors' =  $form- getMessages(),
            'formData'   =  $form- getData(),
        ]);
    }

    return array('form' =  $form);
}

